import React from "react";
import "./Style.css";

import { Link } from "react-router-dom";

export class OptionPage extends React.Component {
  constructor(props) {
    super(props);

    this.startNewGame = this.startNewGame.bind(this);
    this.loadData = this.loadData.bind(this);
  }
  startNewGame() {
    const { startGame, resetTextChoices, resetVariables } = this.props;
    localStorage.setItem("start_second", 0);

    // Dispatch Redux actions
    startGame();
    resetTextChoices();
    resetVariables();

    this.loadData();
  }
  render() {
    return (
      <div>
        <h1 style={{ color: "white", textAlign: "center" }}>WELCOME</h1>
        <div>
          <button className="optionButton">
            <Link to="/game" onClick={this.startNewGame}>
              hgdvsbn
            </Link>
          </button>
          <button>hgdvsbn</button>
        </div>
      </div>
    );
  }
}
export default OptionPage;
