import React, { useState } from "react";
import "./Style.css";
import { Link } from "react-router-dom";

export default function App() {
  let a = JSON.parse(localStorage.getItem("customGame"));
  const [stepId, setstepId] = useState(1);
  return (
    <div>
      {a.map((item) =>
        item.id === stepId ? (
          <>
            <div className="newgame">
              <img
                src={item.image}
                style={{
                  width: "300px",
                  height: "250px",
                  paddingLeft: "500px",
                }}
              />
              <br />
              {!item.end ? (
                <div>
                  {" "}
                  <p styles={{ color: "white", textIndent: "30px" }}>
                    {item.text}
                  </p>
                  <br />{" "}
                </div>
              ) : (
                <h1>{item.text}</h1>
              )}
              {!item.end ? (
                <div>
                  {item.options.map((inter) => (
                    <>
                      <buttton
                        className="choices"
                        onClick={() => {
                          setstepId(inter.next_step);
                        }}
                      >
                        {inter.text}
                      </buttton>
                      <br />
                      <br />
                      <br />
                    </>
                  ))}
                </div>
              ) : (
                <>
                  <img
                    src={"www.google.com"}
                    style={{ width: "300px", height: "250px" }}
                  />
                  <br></br>
                  <div styles={{ height: "500px" }}>
                    {/* <Link to="/"> */}
                    <button className="choices" styles={{ height: "500px" }}>
                      {" "}
                      Return To Menu
                    </button>
                    {/* </Link> */}
                  </div>
                </>
              )}
            </div>
          </>
        ) : null
      )}
    </div>
  );
}
