import React, { useState } from "react";
import "./Style.css";
import { Link } from "react-router-dom";

import { GameNavBar } from "../GamePage/GameNavBar";

export default function App() {
  let a = JSON.parse(localStorage.getItem("customGame"));
  const [stepId, setstepId] = useState(1);
  return (
    <div>
      <GameNavBar />
      {a.map((item) =>
        item.id === stepId ? (
          <>
            <div className="newgame">
              {/* <img
                src={
                  "https://pbs.twimg.com/profile_images/1455185376876826625/s1AjSxph_400x400.jpg"
                }
                style={{
                  width: "300px",
                  height: "250px",
                  paddingLeft: "500px",
                }}
              /> */}
              <img src="https://occ-0-2773-1432.1.nflxso.net/dnm/api/v6/E8vDc_W8CLv7-yMQu8KMEC7Rrr8/AAAABYxJFBDckfZw1YUEIPwyuIg43Kw_HUBLvnCcgdOlvvf5Nc90SF3HSAi5L8uLyBqjziKBY-kGD2wu2JAqVsdHVR0frb6qG26I_U5v.jpg?r=77f" />
              <br />
              {!item.end ? (
                <div>
                  {" "}
                  <p styles={{ color: "white", textIndent: "30px" }}>
                    {item.text}
                  </p>
                  <br />{" "}
                </div>
              ) : (
                <center>
                  <h1>{item.text}</h1>
                </center>
              )}
              {!item.end ? (
                <div>
                  {item.options.map((inter) => (
                    <>
                      <buttton
                        className="choices"
                        onClick={() => {
                          setstepId(inter.next_step);
                        }}
                      >
                        {inter.text}
                      </buttton>
                      <br />
                      <br />
                      <br />
                    </>
                  ))}
                </div>
              ) : (
                <>
                  <br></br>
                  <center>
                    <div styles={{ height: "500px", align: "top" }}>
                      <Link to="/">
                        <button className="returnmenu"> Return To Menu</button>
                      </Link>
                    </div>
                  </center>
                </>
              )}
            </div>
          </>
        ) : null
      )}
    </div>
  );
}
