import "./styles.css";
import React, { useState } from "react";

export default function App() {
  let a = [
    {
      id: 1,
      end: false,
      text: "How Are You?",
      image: "www.google.com",
      options: [
        {
          text: "I m Fine",
          next_step: 4,
        },
        {
          text: "I m not Fine",
          next_step: 3,
        },
      ],
    },
    {
      id: 4,
      end: false,
      text: "Am i Good?",
      image: "www.google.com",
      options: [
        {
          text: "yes",
          next_step: 2,
        },
        {
          text: "No",
          next_step: 3,
        },
      ],
    },
    {
      id: 2,
      end: true,
      text: "You have passed",
    },
    {
      id: 3,
      end: true,
      text: "You have failed",
    },
  ];
  const [stepId, setstepId] = useState(1);

  return (
    <div>
      {a.map((item) =>
        item.id == stepId ? (
          <>
            <h1>{item.text}</h1>
            {!item.end ? (
              <div>
                {item.options.map((inter) => (
                  <>
                    <buttton
                      onClick={() => {
                        setstepId(inter.next_step);
                      }}
                    >
                      {inter.text}
                    </buttton>
                    <br />
                  </>
                ))}
              </div>
            ) : null}
          </>
        ) : null
      )}
    </div>
  );
}
