import React, { useState } from "react";
import "./Style.css";
import { Link } from "react-router-dom";

export default function App() {
  let a = JSON.parse(localStorage.getItem("customGame"));
  const [stepId, setstepId] = useState(1);
  return (
    <div>
      {a.map((item) =>
        item.id === stepId ? (
          <>
            <div className="newgame">
              <img
                src={item.image}
                style={{
                  width: "300px",
                  height: "250px",
                  paddingLeft: "500px",
                }}
              />
              <br />
              <p styles={{ color: "white", textIndent: "30px" }}>{item.text}</p>
              <br />
              {!item.end ? (
                <div>
                  {item.options.map((inter) => (
                    <>
                      <buttton
                        className="choices"
                        onClick={() => {
                          setstepId(inter.next_step);
                        }}
                      >
                        {inter.text}
                      </buttton>
                      <br />
                      <br />
                      <br />
                    </>
                  ))}
                </div>
              ) : (
                <>
                  <Link to="/">
                    <button className="optionButton"> Return To Menu</button>
                  </Link>
                </>
              )}
            </div>
          </>
        ) : null
      )}
    </div>
  );
}
