import React from "react";
import "./Style.css";

export class OptionPage extends React.Component {
  componentDidMount() {
    if (!localStorage.getItem("LEADERBOARDDATA")) {
      localStorage.setItem("LEADERBOARDDATA", JSON.stringify([
        { name: "zahid@gmail.com", score: "39" },
        { name: "za@gmail.com", score: "89" },
      ]));
    }
  }
  render() {
    return (
      <div className="menu-page fade-in">
        {this.state.redirect ? <Redirect to="/login" /> : null}
        <h1 className="menu-tagline" style={{ color: "white" }}>{`Welcome ${
          this.state.name ? this.state.name.split("@")[0] : ""
        }`}</h1>
        <h1 className="menu-title">Security Awareness</h1>
        <h4 className="menu-tagline">An incident response training game</h4>
        <div className="d-flex justify-content-center">
          <p className="menu-info">
            Start the game and help your company from attacks
          </p>
        </div>
        <MenuListContainer />
        <button className="MenuButton">
          <Link
            to="/leadboard"
            className="navbar-brand"
            style={{ color: "cyan" }}
          >
            <h4>Leader Board</h4>
          </Link>
        </button>
        <br></br>
        <br></br>
        <button
          onClick={() => {
            localStorage.removeItem("LOGINDATA");
            window.location.reload();
          }}
          className="MenuButton"
          style={{ color: "cyan" }}
        >
          LogOut
        </button>
      </div>
    );
  }
}
export default OptionPage;
